INSERT INTO [dbo].[TipoTest]([Nombre],[Descripcion],[Instrucciones],[Creado],[Eliminado])VALUES ('Evaluación de Personalidad','Personalidad','<h2 class="card-title">Instrucciones para el Test de Personalidad Big Five</h2><br><h3 class="card-subtitle mb-2 text-muted">Objetivo:</h3><p class="card-text">Este test tiene como finalidad evaluar cinco dimensiones fundamentales de la personalidad: <strong>Extroversión, Responsabilidad, Amabilidad, Neuroticismo y Apertura a la experiencia</strong>. Los resultados pueden ayudarte a conocerte mejor y entender cómo te relacionas con el mundo.</p><h3 class="card-subtitle mb-2 text-muted">Indicaciones:</h3><ul class="list-group list-group-flush"><li class="list-group-item">El test consta de 25 afirmaciones.</li><li class="list-group-item">Cada afirmación debe ser respondida según qué tan de acuerdo estás con ella.</li><li class="list-group-item">No hay respuestas correctas o incorrectas. Sé honesto/a contigo mismo/a.</li><li class="list-group-item">Responde pensando en <strong>cómo eres generalmente</strong>, no en situaciones excepcionales.</li></ul><h3 class="card-subtitle mb-2 text-muted mt-3">Escala de respuesta:</h3><p class="card-text">Marca el número que mejor represente tu nivel de acuerdo con cada afirmación:</p><ul class="list-group list-group-flush"><li class="list-group-item"><strong>1</strong> Totalmente en desacuerdo</li><li class="list-group-item"><strong>2</strong> En desacuerdo</li><li class="list-group-item"><strong>3</strong> Ni de acuerdo ni en desacuerdo</li><li class="list-group-item"><strong>4</strong> De acuerdo</li><li class="list-group-item"><strong>5</strong> Totalmente de acuerdo</li></ul>”.',GETDATE(),0);
