DECLARE @IdTipoTest as int =(select top 1 id from TipoTest where Nombre='Evaluación IQ')

update TipoTest set 
Descripcion ='El Inventario de Depresión de Beck-II (BDI-II) es un cuestionario de autoinforme compuesto por 21 ítems, diseñado para evaluar la presencia y gravedad de síntomas depresivos en adolescentes y adultos (13 años o más). Cada ítem representa un síntoma común de la depresión, como tristeza, pérdida de placer, culpa, fatiga, etc.',
Instrucciones='<h2 class="card-title">Instrucciones Depresión de Beck (BDI-II)</h2><br><h3 class="card-subtitle mb-2 text-muted">Objetivo:</h3><p class="card-text">Este test busca evaluar cómo se ha sentido una persona durante la última semana en relación con síntomas físicos y emocionales de depresión. </p><h3 class="card-subtitle mb-2 text-muted">Indicaciones:</h3><ul class="list-group list-group-flush"><li class="list-group-item">El test consta de 21 preguntas.</li><li class="list-group-item">Cada afirmación debe ser respondida según qué tan de acuerdo estás con ella.</li><li class="list-group-item">No hay respuestas correctas o incorrectas. Sé honesto/a contigo mismo/a.</li><li class="list-group-item">Responde pensando en <strong>cómo eres generalmente</strong>, no en situaciones excepcionales.</li></ul><h3 class="card-subtitle mb-2 text-muted mt-3">Escala de respuesta:</h3><p class="card-text">Marca el número que mejor represente tu nivel de acuerdo con cada afirmación:</p><ul class="list-group list-group-flush"><li class="list-group-item"><strong>1</strong> No en absoluto</li><li class="list-group-item"><strong>2</strong> Levemente (no me molesta mucho)</li><li class="list-group-item"><strong>3</strong> Moderadamente (fue desagradable pero podía soportarlo)</li><li class="list-group-item"><strong>4</strong> Severamente (casi no podía soportarlo)</li></ul>',
Actualizado=getdate()
where Id =@IdTipoTest
select * from TipoTest where Id =@IdTipoTest
