DECLARE @IdTipoTest as int =(select top 1 id from TipoTest where Nombre='Evaluación de Autoestima')

update TipoTest set 
Descripcion ='El Test de Autoestima de Rosenberg tiene como propósito ayudarte a comprender cómo te valoras a ti mismo/a. A través de afirmaciones simples, busca identificar si tu autoestima es alta, media o baja, permitiéndote reflexionar sobre tu bienestar emocional. Es una herramienta útil tanto para profesionales como para quienes desean conocerse mejor, detectar áreas de mejora personal o seguir el progreso en procesos de desarrollo o terapia.',
Instrucciones='<h2 class="card-title">Instrucciones para el  Autoestima de Rosenberg (RSES)</h2><br><h3 class="card-subtitle mb-2 text-muted">Objetivo:</h3><p class="card-text">El Test de Autoestima de Rosenberg tiene como propósito ayudarte a comprender cómo te valoras a ti mismo/a. A través de afirmaciones simples, busca identificar si tu autoestima es alta, media o baja, permitiéndote reflexionar sobre tu bienestar emocional. </p><h3 class="card-subtitle mb-2 text-muted">Indicaciones:</h3><ul class="list-group list-group-flush"><li class="list-group-item">El test consta de 10 preguntas.</li><li class="list-group-item">Cada afirmación debe ser respondida según qué tan de acuerdo estás con ella.</li><li class="list-group-item">No hay respuestas correctas o incorrectas. Sé honesto/a contigo mismo/a.</li><li class="list-group-item">Responde pensando en <strong>cómo eres generalmente</strong>, no en situaciones excepcionales.</li></ul><h3 class="card-subtitle mb-2 text-muted mt-3">Escala de respuesta:</h3><p class="card-text">Marca el número que mejor represente tu nivel de acuerdo con cada afirmación:</p><ul class="list-group list-group-flush"><li class="list-group-item"><strong>1</strong> Totalmente en desacuerdo</li><li class="list-group-item"><strong>2</strong> En desacuerdo</li><li class="list-group-item"><strong>3</strong> Ni de acuerdo ni en desacuerdo</li><li class="list-group-item"><strong>4</strong> De acuerdo</li><li class="list-group-item"><strong>5</strong> Totalmente de acuerdo</li></ul>',
Actualizado=getdate()
where Id =@IdTipoTest
select * from TipoTest where Id =@IdTipoTest
